func (svgb *SagaValueGroupBy) Aggregate(fns ...AggregateFunc) *SagaValueGroupBy {
	svgb.fns = append(svgb.fns, fns...)
	return svgb
}

// Scan applies the selector query and scans the result into the given value.
func (svgb *SagaValueGroupBy) Scan(ctx context.Context, v any) error {
	ctx = setContextOp(ctx, svgb.build.ctx, ent.OpQueryGroupBy)
	if err := svgb.build.prepareQuery(ctx); err != nil {
		return err
	}
	return scanWithInterceptors[*SagaValueQuery, *SagaValueGroupBy](ctx, svgb.build, svgb, svgb.build.inters, v)
}

func (svgb *SagaValueGroupBy) sqlScan(ctx context.Context, root *SagaValueQuery, v any) error {
	selector := root.sqlQuery(ctx).Select()
	aggregation := make([]string, 0, len(svgb.fns))
	for _, fn := range svgb.fns {
		aggregation = append(aggregation, fn(selector))
	}
	if len(selector.SelectedColumns()) == 0 {
		columns := make([]string, 0, len(*svgb.flds)+len(svgb.fns))
		for _, f := range *svgb.flds {
			columns = append(columns, selector.C(f))
		}
		columns = append(columns, aggregation...)
		selector.Select(columns...)
	}
	selector.GroupBy(selector.Columns(*svgb.flds...)...)
	if err := selector.Err(); err != nil {
		return err
	}
	rows := &sql.Rows{}
	query, args := selector.Query()
	if err := svgb.build.driver.Query(ctx, query, args, rows); err != nil {
		return err
	}
	defer rows.Close()
	return sql.ScanSlice(rows, v)
}

// SagaValueSelect is the builder for selecting fields of SagaValue entities.
type SagaValueSelect struct {
	*SagaValueQuery
	selector
}

// Aggregate adds the given aggregation functions to the selector query.
func (svs *SagaValueSelect) Aggregate(fns ...AggregateFunc) *SagaValueSelect {
	svs.fns = append(svs.fns, fns...)
	return svs
}

// Scan applies the selector query and scans the result into the given value.
func (svs *SagaValueSelect) Scan(ctx context.Context, v any) error {
	ctx = setContextOp(ctx, svs.ctx, ent.OpQuerySelect)
	if err := svs.prepareQuery(ctx); err != nil {
		return err
	}
	return scanWithInterceptors[*SagaValueQuery, *SagaValueSelect](ctx, svs.SagaValueQuery, svs, svs.inters, v)
}

func (svs *SagaValueSelect) sqlScan(ctx context.Context, root *SagaValueQuery, v any) error {
	selector := root.sqlQuery(ctx)
	aggregation := make([]string, 0, len(svs.fns))
	for _, fn := range svs.fns {
		aggregation = append(aggregation, fn(selector))
	}
	switch n := len(*svs.selector.flds); {
	case n == 0 && len(aggregation) > 0:
		selector.Select(aggregation...)
	case n != 0 && len(aggregation) > 0:
		selector.AppendSelect(aggregation...)
	}
	rows := &sql.Rows{}
	query, args := selector.Query()
	if err := svs.driver.Query(ctx, query, args, rows); err != nil {
		return err
	}
	defer rows.Close()
	return sql.ScanSlice(rows, v)
}
